import React, {useContext} from 'react'
import styled from "styled-components"

import {GithubContext} from "../../Context/UserContext"
import {ExampleChart, Pie3D, Column3D, Bar3D, Doughnut2D} from "../Charts"

const Repos = () => {
    const {repos} = useContext(GithubContext);

// Reduce to get Languages Only
    let languages = repos.reduce((total, item)=>{
      // Destructure language for every item iterated:
      const {language} = item;
      // If Language == null: Return my object
      if (!language) return total;

      // *** Creating Key-Value Object for each language ***
      // If NO key for "js, html, css, etc" CREATE IT  and assign VALUE 1 (Key - Value)
      if(!total[language]) {
        total[language] = {label: language, value: 1};
      } 
      // If SI existe: Add +1 for each Iteration
      else {
        total[language] = {...total[language], value: total[language].value + 1};
      }
      return total
    },{})
    // Transform LANGUAGES to Array:
    languages = Object.values(languages);
    console.log(languages);

    // STEP 2 - Chart Data
    const chartData = [
      {
        label: "Venezuela",
        value: "290"
      },
      {
        label: "Saudi",
        value: "260"
      },  
      {
        label: "Tom",
        value: "70"
      },  
    ];

    return (
        <section className="section">
            <Wrapper className="section-center">
                {/* <ExampleChart data={chartData}></ExampleChart> */}
                <Pie3D data={chartData}></Pie3D>
            </Wrapper>
        </section>
    )
}

// Styled Components
const Wrapper = styled.div`

`

export default Repos
